/*
SAMPLE USAGE:
--------------
SELECT * FROM set_user_profile_photo('test@fake.com',
  'data:image/jpeg;base64,'
);

DROP FUNCTION set_user_profile_photo(text, BYTEA);
 */

CREATE FUNCTION set_user_profile_photo (p_user_id text, p_b64_image bytea) RETURNS bytea
	LANGUAGE sql
AS $$
   UPDATE user_profile
    SET profile_photo_b64 = p_b64_image
    RETURNING profile_photo_b64

$$
